function calculateVPD(tempAir, tempLeaf, relHum) {
    tempAir = parseFloat(tempAir);
    tempLeaf = parseFloat(tempLeaf);
    relHum = parseFloat(relHum);

    if (isNaN(tempAir) || isNaN(tempLeaf) || isNaN(relHum)) {
        return "unavailable";
    }

    let sdpAir = 0.6108 * Math.exp((17.27 * tempAir) / (tempAir + 237.3));
    let sdpLeaf = 0.6108 * Math.exp((17.27 * tempLeaf) / (tempLeaf + 237.3));
    let adp = (relHum / 100) * sdpAir;
    let vpd = sdpLeaf - adp;

    return vpd.toFixed(2);
}

