var actionTasks;
var timeBetweenTasks;
var fullActionTasks;
var partActionTasks;
var timeOut;

if (lightTask === "turnOn") {
    actionTasks = maxPower / dimmSteps;
} else if (lightTask === "turnOff") {
    actionTasks = powerPercentage / dimmSteps;
}

if ((actionTasks % 1) != 0) {
    partActionTasks = 1;
} else {
    partActionTasks = 0;
}

fullActionTasks = Math.floor(actionTasks)
timeBetweenTasks = sunriseDuration / actionTasks

function sendMessage(updatedPowerPercentage, updatedPowerState) {
    msg.payload = {
        "powerState": updatedPowerState,
        "powerPercentage": updatedPowerPercentage,
        "isDimmable": isDimmable,
        "Area": area
    };
    node.send([msg]);
}

if (lightTask === "turnOn") {
    for (let index = 0; index <= actionTasks; index++) {
        let tempPowerPercentage = dimmSteps * (index +1);
        let timeOut = Math.round(timeBetweenTasks * index);
        let tempPowerState = powerState
        if (index >= 1) {
            tempPowerState = true;
        }
        if (index === fullActionTasks && partActionTasks === 1) {
            tempPowerPercentage = maxPower;
            timeOut = Math.round(timeBetweenTasks * index);
        }
        setTimeout(() => { sendMessage(tempPowerPercentage, tempPowerState); }, timeOut * 1000);
    }
} else if (lightTask === "turnOff") {
    for (let index = 0; index <= actionTasks; index++) {
        let timeOut = Math.round(timeBetweenTasks * index);
        let tempPowerState = powerState;
        let tempPowerPercentage;
        let partActionDimmAmount = powerPercentage - (dimmSteps * fullActionTasks)
        if (index === 0 && partActionTasks != 0) {
            tempPowerPercentage = powerPercentage - partActionDimmAmount;
        } else if (partActionTasks != 0) {
            tempPowerPercentage = powerPercentage - partActionDimmAmount - dimmSteps * index;
        } else {
            tempPowerPercentage = powerPercentage - dimmSteps * index;
        }

        if (index === fullActionTasks && partActionTasks === 1) {
            tempPowerPercentage = 0;
            timeOut = Math.round(timeBetweenTasks * index);
        }
        setTimeout(() => { sendMessage(tempPowerPercentage, tempPowerState); }, timeOut * 1000);
    }
}

